const initialState = {
	isMuted: false,
	isWebcamOff: false,
	isBackCamera: false,
};

export default (state = initialState, action: any) => {
	if (action.type === 'TOGGLEMIC') {
		state = {...state, isMuted: !state.isMuted};
	}

	if (action.type === 'TOGGLEVIDEO') {
		state = {...state, isWebcamOff: !state.isWebcamOff};
	}

	if (action.type === 'TOGGLECAMERADIRECTION') {
		state = {...state, isBackCamera: !state.isBackCamera};
	}

	return state;
};
